        this.http = http;
        this.baseUrl = 'http://localhost:3000/matches';
    }
    // get method returns an Observable<Match[]> for async communication.
    getMatch() {
        return this.http.get(this.baseUrl);
    }
    // http://localhost:37584/api/Product/GetById/4
    getMatchById(id) {
        return this.http.get(this.baseUrl + '/GetById/' + id);
    }
    // fetching the product from the ProductList Component and posting it to the web api.
    createMatch(match) {
        return this.http.post(this.baseUrl, match);
    }
    updateMatch(match) {
        return this.http.put(this.baseUrl + '/' + match.id, match);
    }
    deleteMatch(id) {
        return this.http.delete(this.baseUrl + '/' + id);
    }
}
