        this.baseUrl = 'http://localhost:19169/api/Match';
    }
    // get method returns an Observable<Match[]> for async communication.
    getMatch() {
        return this.httpClient.get(this.baseUrl);
    }
    // http://localhost:37584/api/Match/GetById/4
    getMatchById(id) {
        return this.httpClient.get('http://localhost:19169/api/Match/Get' + id);
    }
    // fetching the match from the MatchList Component and posting it to the web api.
    createMatch(match) {
        return this.httpClient.post('http://localhost:19169/api/Match', match);
    }
    updateMatch(match) {
        return this.httpClient.put(this.baseUrl + '/' + match.id, match);
    }
    deleteMatch(id) {
        return this.httpClient.delete(this.baseUrl + '/' + id);
    }
}
